import React, { useEffect, useState } from "react";

const backendURL = "http://localhost:5000";

function Dashboard() {
  const [products, setProducts] = useState([]);
  const [sales, setSales] = useState([]);

  const fetchData = async () => {
    const resProducts = await fetch(`${backendURL}/products`);
    const dataProducts = await resProducts.json();
    setProducts(dataProducts);

    const resSales = await fetch(`${backendURL}/sales`);
    const dataSales = await resSales.json();
    setSales(dataSales);
  };

  useEffect(() => {
    fetchData();
  }, []);

  const totalProducts = products.length;
  const lowStock = products.filter((p) => p.quantity < 10).length;
  const totalSales = sales.length;

  return (
    <div className="dashboard">
      <h1>Dashboard Module</h1>
      <div className="dashboard-cards" style={{ display: "flex", gap: "20px" }}>
        <div className="card" style={{ border: "1px solid #ccc", padding: "20px" }}>
          <h2>Total Products</h2>
          <p>{totalProducts}</p>
        </div>
        <div className="card" style={{ border: "1px solid #ccc", padding: "20px" }}>
          <h2>Low Stock</h2>
          <p>{lowStock}</p>
        </div>
        <div className="card" style={{ border: "1px solid #ccc", padding: "20px" }}>
          <h2>Total Sales</h2>
          <p>{totalSales}</p>
        </div>
      </div>
    </div>
  );
}

export default Dashboard;
