import React, { useEffect, useState } from "react";
import "../styles/Inventory.css";

const backendURL = "http://localhost:5000";

function Inventory() {
  const [products, setProducts] = useState([]);
  const [stockTransactions, setStockTransactions] = useState([]);
  const [stockInput, setStockInput] = useState({}); // { productId: quantity }

  const fetchData = async () => {
    const resProducts = await fetch(`${backendURL}/products`);
    setProducts(await resProducts.json());

    const resStock = await fetch(`${backendURL}/stock-transactions`);
    setStockTransactions(await resStock.json());
  };

  useEffect(() => {
    fetchData();
  }, []);

  const addStock = async (productId) => {
    const quantity = Number(stockInput[productId]);
    if (!quantity || quantity <= 0) return alert("Enter a valid quantity");

    await fetch(`${backendURL}/products/${productId}/add-stock`, {
      method: "PATCH",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({ quantity }),
    });

    setStockInput({ ...stockInput, [productId]: "" });
    fetchData();
  };

  return (
    <div>
      <h1>Inventory Module</h1>
      <table>
        <thead>
          <tr>
            <th>Product</th>
            <th>Quantity in Stock</th>
            <th>Add Stock</th>
          </tr>
        </thead>
        <tbody>
          {products.map((p) => (
            <tr key={p.id}>
              <td>{p.name}</td>
              <td>{p.quantity}</td>
              <td>
                <input
                  type="number"
                  value={stockInput[p.id] || ""}
                  onChange={(e) =>
                    setStockInput({ ...stockInput, [p.id]: e.target.value })
                  }
                />
                <button onClick={() => addStock(p.id)}>Add</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>

      <h2>Stock Additions History</h2>
      <table>
        <thead>
          <tr>
            <th>Product</th>
            <th>Quantity Added</th>
            <th>Date</th>
          </tr>
        </thead>
        <tbody>
          {stockTransactions.map((t) => (
            <tr key={t.id}>
              <td>{t.productName}</td>
              <td>{t.quantity}</td>
              <td>{new Date(t.date).toLocaleString()}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}

export default Inventory;
