import React, { useState, useEffect } from "react";

const backendURL = "http://localhost:5000";

function ProductManagement() {
  const [products, setProducts] = useState([]);
  const [newProduct, setNewProduct] = useState({
    name: "",
    description: "",
    category: "",
    price: "",
    quantity: "",
    image: ""
  });

  // Fetch products from backend
  const fetchProducts = async () => {
    try {
      const res = await fetch(`${backendURL}/products`);
      const data = await res.json();
      setProducts(data);
    } catch (err) {
      console.error("Error fetching products:", err);
    }
  };

  useEffect(() => {
    fetchProducts();
  }, []);

  // Handle form input
  const handleChange = (e) => {
    setNewProduct({ ...newProduct, [e.target.name]: e.target.value });
  };

  // Add new product
  const handleAddProduct = async (e) => {
    e.preventDefault();
    try {
      const res = await fetch(`${backendURL}/products`, {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(newProduct)
      });
      const data = await res.json();
      setProducts([...products, data.product]);
      setNewProduct({ name: "", description: "", category: "", price: "", quantity: "", image: "" });
    } catch (err) {
      console.error("Error adding product:", err);
    }
  };

  return (
    <div>
      <h2>Product Management</h2>

      {/* Products Table on Top */}
      <table style={{ width: "100%", marginBottom: "20px", borderCollapse: "collapse" }}>
        <thead>
          <tr>
            <th style={{ border: "1px solid #ccc", padding: "8px" }}>Name</th>
            <th style={{ border: "1px solid #ccc", padding: "8px" }}>Description</th>
            <th style={{ border: "1px solid #ccc", padding: "8px" }}>Category</th>
            <th style={{ border: "1px solid #ccc", padding: "8px" }}>Price</th>
            <th style={{ border: "1px solid #ccc", padding: "8px" }}>Quantity</th>
            <th style={{ border: "1px solid #ccc", padding: "8px" }}>Image URL</th>
          </tr>
        </thead>
        <tbody>
          {products.map((p) => (
            <tr key={p.id}>
              <td style={{ border: "1px solid #ccc", padding: "8px" }}>{p.name}</td>
              <td style={{ border: "1px solid #ccc", padding: "8px" }}>{p.description}</td>
              <td style={{ border: "1px solid #ccc", padding: "8px" }}>{p.category}</td>
              <td style={{ border: "1px solid #ccc", padding: "8px" }}>${p.price}</td>
              <td style={{ border: "1px solid #ccc", padding: "8px" }}>{p.quantity}</td>
              <td style={{ border: "1px solid #ccc", padding: "8px" }}>{p.image}</td>
            </tr>
          ))}
        </tbody>
      </table>

      {/* Add Product Form below the table */}
      <form onSubmit={handleAddProduct} style={{ display: "flex", flexDirection: "column", gap: "10px" }}>
        <input
          type="text"
          name="name"
          placeholder="Name"
          value={newProduct.name}
          onChange={handleChange}
          required
        />
        <input
          type="text"
          name="description"
          placeholder="Description"
          value={newProduct.description}
          onChange={handleChange}
          required
        />
        <input
          type="text"
          name="category"
          placeholder="Category"
          value={newProduct.category}
          onChange={handleChange}
          required
        />
        <input
          type="number"
          name="price"
          placeholder="Price"
          value={newProduct.price}
          onChange={handleChange}
          required
        />
        <input
          type="number"
          name="quantity"
          placeholder="Quantity"
          value={newProduct.quantity}
          onChange={handleChange}
          required
        />
        <input
          type="text"
          name="image"
          placeholder="Image URL (e.g., src/assets/image.png)"
          value={newProduct.image}
          onChange={handleChange}
        />
        <button type="submit" style={{ padding: "10px", backgroundColor: "#ffb6c1", color: "#fff", border: "none", borderRadius: "5px" }}>
          Add Product
        </button>
      </form>
    </div>
  );
}

export default ProductManagement;
