import React, { useEffect, useState } from "react";
import '../styles/Dashboard.css';

const backendURL = "http://localhost:5000";

function Dashboard() {
  const [products, setProducts] = useState([]);
  const [sales, setSales] = useState([]);

  const fetchData = async () => {
    try {
      const resProducts = await fetch(`${backendURL}/products`);
      const dataProducts = await resProducts.json();
      setProducts(dataProducts);

      const resSales = await fetch(`${backendURL}/sales`);
      const dataSales = await resSales.json();
      setSales(dataSales);
    } catch (err) {
      console.error("Error fetching data:", err);
    }
  };

  useEffect(() => {
    fetchData();
  }, []);

  const totalProducts = products.length;
  const lowStock = products.filter((p) => p.quantity < 10).length;
  const totalSales = sales.length;

  return (
    <div className="dashboard" style={{ padding: "20px" }}>
      <h1>Dashboard Module</h1>

      {/* Summary Cards */}
      <div
        className="dashboard-cards"
        style={{ display: "flex", gap: "20px", marginBottom: "40px" }}
      >
        <div
          className="card"
          style={{ border: "1px solid #ccc", padding: "20px", flex: 1 }}
        >
          <h2>Total Products</h2>
          <p>{totalProducts}</p>
        </div>
        <div
          className="card"
          style={{ border: "1px solid #ccc", padding: "20px", flex: 1 }}
        >
          <h2>Low Stock</h2>
          <p>{lowStock}</p>
        </div>
        <div
          className="card"
          style={{ border: "1px solid #ccc", padding: "20px", flex: 1 }}
        >
          <h2>Total Sales</h2>
          <p>{totalSales}</p>
        </div>
      </div>

      {/* Products Display */}
      <h2>Products</h2>
      <div
        className="products"
        style={{ display: "flex", flexWrap: "wrap", gap: "20px" }}
      >
        {products.map((product) => (
          <div
            key={product.id}
            className="product-card"
            style={{
              border: "1px solid #ccc",
              padding: "10px",
              width: "200px",
              textAlign: "center",
            }}
          >
            <img
              src={product.image}
              alt={product.name}
              style={{ width: "100%", height: "150px", objectFit: "cover" }}
            />
            <h3>{product.name}</h3>
            <p>Price: ${product.price}</p>
            <p>Quantity: {product.quantity}</p>
          </div>
        ))}
      </div>
    </div>
  );
}

export default Dashboard;
