import React, { useEffect, useState } from "react";
import '../styles/Inventory.css';

const backendURL = "http://localhost:5000";

function Inventory() {
  const [products, setProducts] = useState([]);
  const [stockInputs, setStockInputs] = useState({});
  const [stockHistory, setStockHistory] = useState([]); // track stock additions

  // Fetch products and stock transactions
  const fetchData = async () => {
    try {
      const resProducts = await fetch(`${backendURL}/products`);
      const dataProducts = await resProducts.json();
      setProducts(dataProducts);

      const resStock = await fetch(`${backendURL}/stock-transactions`);
      const dataStock = await resStock.json();
      setStockHistory(dataStock);
    } catch (err) {
      console.error("Error fetching data:", err);
    }
  };

  useEffect(() => {
    fetchData();
  }, []);

  const handleStockChange = (productId, value) => {
    setStockInputs({ ...stockInputs, [productId]: value });
  };

  const handleAddStock = async (productId) => {
    const quantity = Number(stockInputs[productId]);
    if (!quantity || quantity <= 0) {
      alert("Please enter a valid quantity");
      return;
    }

    try {
      const res = await fetch(`${backendURL}/products/${productId}/add-stock`, {
        method: "PATCH",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ quantity }),
      });

      const data = await res.json();
      if (res.ok) {
        alert("Stock added successfully!");
        setStockInputs({ ...stockInputs, [productId]: "" });
        fetchData(); // refresh both products and stock history
      } else {
        alert(data.error || "Error adding stock");
      }
    } catch (err) {
      console.error("Error adding stock:", err);
    }
  };

  return (
    <div className="inventory" style={{ padding: "20px" }}>
      <h1>Inventory Module</h1>

      {/* Products Table */}
      <table className="inventory-table">
        <thead>
          <tr>
            <th>Product Name</th>
            <th>Price ($)</th>
            <th>Current Quantity</th>
            <th>Add Stock</th>
            <th>Action</th>
          </tr>
        </thead>
        <tbody>
          {products.map(product => (
            <tr key={product.id}>
              <td>{product.name}</td>
              <td>{product.price}</td>
              <td>{product.quantity}</td>
              <td>
                <input
                  type="number"
                  min="1"
                  value={stockInputs[product.id] || ""}
                  onChange={e => handleStockChange(product.id, e.target.value)}
                />
              </td>
              <td>
                <button onClick={() => handleAddStock(product.id)}>Add</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>

      {/* Stock History */}
      <h2 style={{ marginTop: "40px" }}>Stock Additions History</h2>
      <table className="inventory-table">
        <thead>
          <tr>
            <th>Product</th>
            <th>Quantity Added</th>
            <th>Date</th>
          </tr>
        </thead>
        <tbody>
          {stockHistory.map((entry) => (
            <tr key={entry.id}>
              <td>{entry.productName}</td>
              <td>{entry.quantity}</td>
              <td>{new Date(entry.date).toLocaleString()}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}

export default Inventory;
