import React, { useEffect, useState } from "react";

const API_URL = "http://localhost:5000";

function ProductManagement() {
  const [products, setProducts] = useState([]);
  const [formData, setFormData] = useState({
    name: "",
    description: "",
    category: "",
    price: "",
    quantity: "",
    image: ""
  });

  // Fetch products from backend
  const fetchProducts = async () => {
    const res = await fetch(`${API_URL}/products`);
    const data = await res.json();
    setProducts(data);
  };

  useEffect(() => {
    fetchProducts();
  }, []);

  // Handle form change
  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  // Add product
  const handleAddProduct = async () => {
    await fetch(`${API_URL}/products`, {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify(formData),
    });
    setFormData({
      name: "",
      description: "",
      category: "",
      price: "",
      quantity: "",
      image: ""
    });
    fetchProducts();
  };

  // Delete product
  const handleDelete = async (id) => {
    await fetch(`${API_URL}/products/${id}`, { method: "DELETE" });
    fetchProducts();
  };

  return (
    <div>
      <h2>Product Management</h2>
      
      <div className="form">
        <input name="name" placeholder="Name" value={formData.name} onChange={handleChange} />
        <input name="description" placeholder="Description" value={formData.description} onChange={handleChange} />
        <input name="category" placeholder="Category" value={formData.category} onChange={handleChange} />
        <input name="price" placeholder="Price" type="number" value={formData.price} onChange={handleChange} />
        <input name="quantity" placeholder="Quantity" type="number" value={formData.quantity} onChange={handleChange} />
        <input name="image" placeholder="Image filename" value={formData.image} onChange={handleChange} />
        <button onClick={handleAddProduct}>Add Product</button>
      </div>

      <table border="1" cellPadding="5">
        <thead>
          <tr>
            <th>Name</th><th>Category</th><th>Price</th><th>Quantity</th><th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {products.map(p => (
            <tr key={p.id}>
              <td>{p.name}</td>
              <td>{p.category}</td>
              <td>{p.price}</td>
              <td>{p.quantity}</td>
              <td>
                <button onClick={() => handleDelete(p.id)}>Delete</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}

export default ProductManagement;
