import React, { useEffect, useState } from "react";
import '../styles/Inventory.css';

const backendURL = "http://localhost:5000";

function Inventory() {
  const [products, setProducts] = useState([]);
  const [stockQty, setStockQty] = useState({});
  const [stockLog, setStockLog] = useState([]);

  const fetchProducts = async () => {
    try {
      const res = await fetch(`${backendURL}/products`);
      const data = await res.json();
      setProducts(data);
    } catch (err) {
      console.error("Error fetching products:", err);
    }
  };

  const fetchStockTransactions = async () => {
    try {
      const res = await fetch(`${backendURL}/stock-transactions`);
      const data = await res.json();
      setStockLog(data);
    } catch (err) {
      console.error("Error fetching stock transactions:", err);
    }
  };

  const handleAddStock = async (productId) => {
    const quantity = Number(stockQty[productId]);
    if (!quantity || quantity <= 0) return alert("Enter a valid quantity");

    try {
      await fetch(`${backendURL}/products/${productId}/add-stock`, {
        method: "PATCH",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ quantity }),
      });
      setStockQty({ ...stockQty, [productId]: "" });
      fetchProducts();
      fetchStockTransactions();
    } catch (err) {
      console.error(err);
    }
  };

  useEffect(() => {
    fetchProducts();
    fetchStockTransactions();
  }, []);

  return (
    <div className="inventory">
      <h1>Inventory Module</h1>

      <div className="products">
        {products.map((product) => (
          <div key={product.id} className="inventory-card">
            <img src={product.image} alt={product.name} />
            <h3>{product.name}</h3>
            <p>Quantity: {product.quantity}</p>
            <input
              type="number"
              min="1"
              placeholder="Add Stock"
              value={stockQty[product.id] || ""}
              onChange={(e) =>
                setStockQty({ ...stockQty, [product.id]: e.target.value })
              }
            />
            <button onClick={() => handleAddStock(product.id)}>Add Stock</button>
          </div>
        ))}
      </div>

      <h2>Stock Transactions</h2>
      <div className="stock-log">
        {stockLog.map((log) => (
          <div key={log.id} className="stock-log-card">
            <p>
              {log.productName}: +{log.quantity} (on{" "}
              {new Date(log.date).toLocaleString()})
            </p>
          </div>
        ))}
      </div>
    </div>
  );
}

export default Inventory;
