import React, { useState } from "react";
import '../styles/Dashboard.css';

import ProductManagement from "./components/ProductManagement";
import Sales from "./components/Sales";
import Inventory from "./components/Inventory";
import Reporting from "./components/Reporting";
import "./styles/App.css"; // Make sure this exists

function App() {
  const [activeModule, setActiveModule] = useState("Dashboard");

  const renderModule = () => {
    switch (activeModule) {
      case "Dashboard":
        return <Dashboard />;
      case "ProductManagement":
        return <ProductManagement />;
      case "Sales":
        return <Sales />;
      case "Inventory":
        return <Inventory />;
      case "Reporting":
        return <Reporting />;
      default:
        return <Dashboard />;
    }
  };

  return (
    <div className="app-container">
      <nav
        className="navbar"
        style={{ display: "flex", gap: "10px", padding: "10px", background: "#f8f0f8" }}
      >
        <button onClick={() => setActiveModule("Dashboard")}>Dashboard</button>
        <button onClick={() => setActiveModule("ProductManagement")}>Products</button>
        <button onClick={() => setActiveModule("Sales")}>Sales</button>
        <button onClick={() => setActiveModule("Inventory")}>Inventory</button>
        <button onClick={() => setActiveModule("Reporting")}>Reporting</button>
      </nav>
      <main className="module-container" style={{ padding: "20px" }}>
        {renderModule()}
      </main>
    </div>
  );
}

export default App;
