import React, { useState, useEffect } from "react";

const backendURL = "http://localhost:5000";

function Sales() {
  const [salesLog, setSalesLog] = useState([]);
  const [products, setProducts] = useState([]);
  const [newSale, setNewSale] = useState({ productId: "", quantity: "" });
  const [editSaleId, setEditSaleId] = useState(null);

  // Fetch sales and products
  const fetchData = async () => {
    const resProducts = await fetch(`${backendURL}/products`);
    const dataProducts = await resProducts.json();
    setProducts(dataProducts);

    const resSales = await fetch(`${backendURL}/sales`);
    const dataSales = await resSales.json();
    setSalesLog(dataSales);
  };

  useEffect(() => {
    fetchData();
  }, []);

  const handleChange = (e) => {
    setNewSale({ ...newSale, [e.target.name]: e.target.value });
  };

  const handleAddOrUpdateSale = async (e) => {
    e.preventDefault();
    if (!newSale.productId || !newSale.quantity) return alert("Fill all fields");

    if (editSaleId) {
      // Update sale
      await fetch(`${backendURL}/sales/${editSaleId}`, {
        method: "PATCH",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(newSale)
      });
      setEditSaleId(null);
    } else {
      // Add sale
      await fetch(`${backendURL}/sales`, {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(newSale)
      });
    }

    setNewSale({ productId: "", quantity: "" });
    fetchData();
  };

  const handleEdit = (sale) => {
    setEditSaleId(sale.id);
    setNewSale({ productId: sale.productId, quantity: sale.quantity });
  };

  const handleDelete = async (id) => {
    await fetch(`${backendURL}/sales/${id}`, { method: "DELETE" });
    fetchData();
  };

  return (
    <div className="sales-management">
      <h1>Sales Module</h1>

      <form onSubmit={handleAddOrUpdateSale} style={{ marginBottom: "20px" }}>
        <select
          name="productId"
          value={newSale.productId}
          onChange={handleChange}
          required
        >
          <option value="">Select Product</option>
          {products.map((p) => (
            <option key={p.id} value={p.id}>
              {p.name} (Stock: {p.quantity})
            </option>
          ))}
        </select>
        <input
          type="number"
          name="quantity"
          placeholder="Quantity"
          value={newSale.quantity}
          onChange={handleChange}
          required
        />
        <button type="submit">{editSaleId ? "Update Sale" : "Add Sale"}</button>
      </form>

      <table border="1" cellPadding="10">
        <thead>
          <tr>
            <th>Product</th>
            <th>Quantity</th>
            <th>Date</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {salesLog.map((s) => (
            <tr key={s.id}>
              <td>{s.productName}</td>
              <td>{s.quantity}</td>
              <td>{new Date(s.date).toLocaleString()}</td>
              <td>
                <button onClick={() => handleEdit(s)}>Edit</button>
                <button onClick={() => handleDelete(s.id)}>Delete</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}

export default Sales;
