import React, { useState, useEffect } from "react";
import '../styles/Inventory.css';

const backendURL = "http://localhost:5000";

function Inventory() {
  const [products, setProducts] = useState([]);
  const [stockTransactions, setStockTransactions] = useState([]);
  const [selectedProduct, setSelectedProduct] = useState("");
  const [quantityToAdd, setQuantityToAdd] = useState("");

  // Fetch products and stock history
  const fetchData = async () => {
    try {
      const resProducts = await fetch(`${backendURL}/products`);
      const dataProducts = await resProducts.json();
      setProducts(dataProducts);

      const resStock = await fetch(`${backendURL}/stock-transactions`);
      const dataStock = await resStock.json();
      setStockTransactions(dataStock);
    } catch (err) {
      console.error("Error fetching data:", err);
    }
  };

  useEffect(() => {
    fetchData();
  }, []);

  // Add stock handler
  const handleAddStock = async () => {
    if (!selectedProduct || !quantityToAdd) return alert("Select a product and enter quantity.");

    try {
      const res = await fetch(`${backendURL}/products/${selectedProduct}/add-stock`, {
        method: "PATCH",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ quantity: Number(quantityToAdd) }),
      });

      const data = await res.json();
      if (res.ok) {
        setQuantityToAdd("");
        setSelectedProduct("");
        fetchData(); // Refresh products and stock history
      } else {
        alert(data.error);
      }
    } catch (err) {
      console.error("Error adding stock:", err);
    }
  };

  return (
    <div className="inventory" style={{ padding: "20px" }}>
      <h1>Inventory Module</h1>

      {/* Add Stock Form */}
      <div className="add-stock-form" style={{ marginBottom: "30px" }}>
        <select
          value={selectedProduct}
          onChange={(e) => setSelectedProduct(e.target.value)}
          style={{ marginRight: "10px", padding: "5px" }}
        >
          <option value="">Select Product</option>
          {products.map((p) => (
            <option key={p.id} value={p.id}>{p.name}</option>
          ))}
        </select>
        <input
          type="number"
          placeholder="Quantity"
          value={quantityToAdd}
          onChange={(e) => setQuantityToAdd(e.target.value)}
          style={{ marginRight: "10px", padding: "5px" }}
        />
        <button onClick={handleAddStock} style={{ padding: "5px 10px" }}>Add Stock</button>
      </div>

      {/* Products Table */}
      <h2>Products</h2>
      <table className="products-table" border="1" cellPadding="10" style={{ marginBottom: "30px", width: "100%" }}>
        <thead>
          <tr>
            <th>Name</th>
            <th>Description</th>
            <th>Category</th>
            <th>Price</th>
            <th>Quantity</th>
          </tr>
        </thead>
        <tbody>
          {products.map((p) => (
            <tr key={p.id}>
              <td>{p.name}</td>
              <td>{p.description}</td>
              <td>{p.category}</td>
              <td>${p.price}</td>
              <td>{p.quantity}</td>
            </tr>
          ))}
        </tbody>
      </table>

      {/* Stock Additions History */}
      <h2>Stock Additions History</h2>
      <table className="stock-history-table" border="1" cellPadding="10" style={{ width: "100%" }}>
        <thead>
          <tr>
            <th>Product</th>
            <th>Quantity Added</th>
            <th>Date</th>
          </tr>
        </thead>
        <tbody>
          {stockTransactions.map((t) => (
            <tr key={t.id}>
              <td>{t.productName}</td>
              <td>{t.quantity}</td>
              <td>{new Date(t.date).toLocaleString()}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}

export default Inventory;
